import UIKit

class ViewController: UIViewController, UICollectionViewDelegate, UICollectionViewDataSource, UICollectionViewDelegateFlowLayout {
	private let viewModel = MovieViewModel()
	
	let images = [
		"https://cdn.ome.lt/legacy/images/galerias/Um-Sonho-de-Liberdade/Um-Sonho-de-Liberdade-10.jpg",
		"https://br.web.img3.acsta.net/medias/nmedia/18/90/93/20/20120876.jpg",
		"https://efemeridesdoefemello.files.wordpress.com/2014/12/20dez141.jpg",
		"https://radiopeaobrasil.com.br/wp-content/uploads/2018/09/batmanjoker-1.jpg",
		"data:image/jpeg;base64,",
		"https://classic.exame.com/wp-content/uploads/2018/04/1510927534-0.jpg?quality=70&strip=info&w=680",
		"https://m.media-amazon.com/images/M/MV5BMjAyMjUyNzg1Ml5BMl5BanBnXkFtZTgwNzAwMzg5MTE@._V1_.jpg",
		"https://www.magazine-hd.com/apps/wp/wp-content/uploads/2018/03/gollum.jpg",
		"https://www.planocritico.com/wp-content/uploads/9015/08/Plano-Cr%C3%ADtico-Tr%C3%AAs-Homens-em-Conflito.jpg",
		"https://classic.exame.com/wp-content/uploads/2017/11/clube-da-luta2.jpg?quality=70&strip=info&w=1024",
		"data:image/jpeg;base64,",
		"https://www.elhombre.com.br/wp-content/uploads/2021/06/forrest-gump-1.jpg"
	]
	
	private let topCircle = {
		let circle = Circle()
		return circle
	}()
	
	private let bottomCircle = {
		let circle = Circle()
		return circle
	}()
	
	private let collectionView = UICollectionView(
		frame: .zero,
		collectionViewLayout: UICollectionViewFlowLayout()
	)
	
	override func viewDidLoad() {
		super.viewDidLoad()
		
		collectionView.register(
			MovieCollectionViewCell.self,
			forCellWithReuseIdentifier: MovieCollectionViewCell.identifier
		)
		
		collectionView.delegate = self
		collectionView.dataSource = self
		
		setup()
	}
	
	override func viewDidLayoutSubviews() {
		super.viewDidLayoutSubviews()
		setupConstraints()
	}
	
	func setup() {
		view.addSubviews(topCircle, bottomCircle, collectionView)
		
		view.backgroundColor = .black
		
		collectionView.backgroundColor = .init(
			red: 40 / 255,
			green: 40 / 255,
			blue: 40 / 255,
			alpha: 0
		)
	}
		
	func setupConstraints() {
		topCircle.anchor(
			top: view.topAnchor,
			leading: nil,
			bottom: nil,
			trailing: view.trailingAnchor,
			padding: .init(top: -300, left: 0, bottom: 0, right: -250),
			size: .init(width: 500, height: 500)
		)
		
		bottomCircle.anchor(
			top: nil,
			leading: view.leadingAnchor,
			bottom: view.bottomAnchor,
			trailing: nil,
			padding: .init(top: 0, left: -250, bottom: -300, right: 0),
			size: .init(width: 500, height: 500)
		)
		
		collectionView.frame = view.bounds
	}

	func collectionView(_ collectionView: UICollectionView, numberOfItemsInSection section: Int) -> Int {
		return viewModel.getMovies().count
	}
	
	func collectionView(_ collectionView: UICollectionView, cellForItemAt indexPath: IndexPath) -> UICollectionViewCell {
		let cell = collectionView.dequeueReusableCell(
			withReuseIdentifier: MovieCollectionViewCell.identifier,
			for: indexPath
		) as! MovieCollectionViewCell
		
		cell.movie = viewModel.getMovies()[indexPath.item]
		
		if indexPath.item < images.count && cell.movie != nil {
			cell.movie?.imageUrl = images[indexPath.row]
		}
		
		return cell
	}
	
	func collectionView(_ collectionView: UICollectionView, layout collectionViewLayout: UICollectionViewLayout, sizeForItemAt indexPath: IndexPath) -> CGSize {
		let width = view.frame.width / 3 - 11
		return CGSize(width: width, height: width * 1.6)
	}
	
	func collectionView(_ collectionView: UICollectionView, layout collectionViewLayout: UICollectionViewLayout, minimumLineSpacingForSectionAt section: Int) -> CGFloat {
		return 10
	}
	
	func collectionView(_ collectionView: UICollectionView, layout collectionViewLayout: UICollectionViewLayout, minimumInteritemSpacingForSectionAt section: Int) -> CGFloat {
		return 1
	}
	
	func collectionView(_ collectionView: UICollectionView, layout collectionViewLayout: UICollectionViewLayout, insetForSectionAt section: Int) -> UIEdgeInsets {
		return UIEdgeInsets(top: 1, left: 5, bottom: 1, right: 5)
	}
	
	func collectionView(_ collectionView: UICollectionView, didSelectItemAt indexPath: IndexPath) {
		collectionView.deselectItem(at: indexPath, animated: true)
		print("\(indexPath.item)")
	}
}
